import { Component, OnInit} from '@angular/core';              // Importe les décorateurs 'Component' et 'OnInit' d'Angular Core.                        // Importe 'Observable' de la bibliothèque RxJS pour la gestion des données asynchrones.
import { OlympicService } from 'src/app/core/services/olympic.service';
import { OlympicCountry } from 'src/app/core/models/Olympic';   // Importe l'interface 'OlympicCountry' pour définir la structure des données olympiques.
import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router'; // Importez ActivatedRoute depuis '@angular/router'

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss']
})
export class DetailsComponent implements OnInit {
  countryName: string = '';
  numberOfEntries: number = 0;
  totalNumberMedals: number = 0;
  totalNumberOfAthletes: number = 0;

  constructor(private activatedRoute: ActivatedRoute, private olympicService: OlympicService, private router: Router) {}
 // Injectez ActivatedRoute 

  ngOnInit(): void {
    // Récupérez le nom du pays à partir des paramètres d'URL
    this.activatedRoute.params.subscribe((params) => {
      const countryName = params['countryName']; // Assurez-vous que le nom correspond à celui que vous avez défini dans votre route.
      // Utilisez le nom du pays comme titre H1 ou stockez-le dans une propriété pour l'afficher dans le template.
      this.countryName = countryName;
    });
  }

}
